import { put, call, takeEvery, all, fork } from "redux-saga/effects";
import {
	GET_WEATHER_DATA,
	SEARCH_WEATHER_DATA,
	SET_WEATHER_DATA,
} from "../actions_type";

function* getWeather(query: string): any {
	let data = yield fetch(
		`https://api.openweathermap.org/data/2.5/weather?q=${query}&appid=b5aad4fab443b8fc561cd84cbc232cc9`
	);
	data = yield data.json();
	console.warn("action PRODUCT_LIST_CALLED called", data);
	yield put({ type: SEARCH_WEATHER_DATA, data });
}

function* searchProducts(data: any): any {
	let result = yield fetch(
		`https://api.openweathermap.org/data/2.5/weather?q=${data.query}&appid=b5aad4fab443b8fc561cd84cbc232cc9`
	);
	result = yield result.json();
	console.warn("action PRODUCT_LIST_CALLED called", result);
	yield put({ type: SEARCH_WEATHER_DATA, data: result });
}

function* productSaga() {
	yield takeEvery(SEARCH_WEATHER_DATA, searchProducts);
}
export default productSaga;
