import * as actions from "../actions_type";

export const setWeather = (payload: actions.WeatherData) => {
	return {
		type: actions.SET_WEATHER_DATA,
		payload,
	};
};

export function getWeather(query: string): actions.GetWeatherAction {
	return {
		type: actions.GET_WEATHER_DATA,
		query,
	};
}
export function getWeatherRequest(): actions.GetWeatherRequestAction {
	return {
		type: actions.GET_WEATHER_REQUEST,
	};
}

export function getWeatherSuccess(
	payload: actions.WeatherData
): actions.GetWeatherSuccessAction {
	return {
		type: actions.GET_WEATHER_SUCCESS,
		payload,
	};
}

export function getWeatherError(
	error: Error | string
): actions.GetWeatherErrorAction {
	return {
		type: actions.GET_WEATHER_ERROR,
		error,
	};
}
