import { put, takeEvery } from "redux-saga/effects";
import {
	GET_WEATHER_DATA,
	SEARCH_WEATHER_DATA,
	SET_WEATHER_DATA,
} from "../actions_type";

function* getWeather(): any {
	let data = yield fetch(
		`https://api.openweathermap.org/data/2.5/weather?q=${query}&appid=b5aad4fab443b8fc561cd84cbc232cc9`
	);
	data = yield data.json();
	console.warn("action get weather called", data);
}

function* searchProducts(data: any): any {
	let result = yield fetch(
		`https://api.openweathermap.org/data/2.5/weather?q=${data.query}&appid=b5aad4fab443b8fc561cd84cbc232cc9`
	);
	result = yield result.json();
	console.warn("action search weather called", result);
	yield put({ type: SET_WEATHER_DATA, data: result });
	yield put({ type: GET_WEATHER_DATA, data });
}

function* weatherSaga() {
	yield takeEvery(GET_WEATHER_DATA, getWeather);
	yield takeEvery(SEARCH_WEATHER_DATA, searchProducts);
}
export default weatherSaga;
