import React, { ChangeEventHandler, useState } from "react";
import { useDispatch } from "react-redux";
import axios from "axios";
import { WeatherActions, WeatherData } from "../redux/actions_type";
import { getWeather, setWeather } from "../redux/searchWeather/weatherActions";
import ButtonHeader from "./button_header";
import InputField from "./inputField_component";
import "../App.css";

const Header = () => {
	const dispatch = useDispatch();
	const [query, setQuery] = useState("");
	const btnClick = async (e: MouseEvent) => {
		e.preventDefault();
		console.log("btn clicked, ", query);
		await axios
			.get(
				`https://api.openweathermap.org/data/2.5/weather?q=${query}&appid=b5aad4fab443b8fc561cd84cbc232cc9`
			)
			.then((data: any) => {
				const formatedData: WeatherData = {
					id: data.id,
					weather: data.weather[0].main,
					temp: data.main.temp,
					humidity: data.main.humidity,
					country: data.name,
					wind: data.wind.speed,
				};
				console.log("query clicked", data);

				dispatch(setWeather(formatedData));
			});
	};
	const handleTextChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		const { value } = e.target as unknown as { value: string };
		console.log("value is ", value);
		console.log("Query is ", query);
		setQuery(value);
	};

	return (
		<div className="header">
			<div className="search-box">
				<InputField
					placeholder="Search example: City, Country"
					handleChange={(e: React.ChangeEvent<HTMLInputElement>) =>
						handleTextChange(e)
					}
				/>
			</div>
			<div className="btn-box">
				<ButtonHeader btnName="weather" handleClick={btnClick} />
			</div>
		</div>
	);
};

export default Header;
