import React, { useEffect, useState } from "react";
import { WeatherData } from "../redux/actions_type";
import { searchWeatherData } from "../redux/searchWeather/weatherActions";
import { useSelector, useDispatch } from "react-redux";

type Props = {
	data: WeatherData[] | [];
};
let storedFavorites: string | null = localStorage.getItem("favoritesWeather");

const FavoritesWeatherList = ({ data }: Props) => {
	const dispatch = useDispatch();
	const [favoriteList, setFavoriteList] = useState<WeatherData[]>([]);

	const checkFavoriteWeather = (query: string) => {
		dispatch(searchWeatherData(query));
	};

	const removeFromFavorite = () => {
		const newList = favoriteList.filter((fav) => fav.id !== data[0].id);
		setFavoriteList([...newList]);
	};

	useEffect(() => {
		if (storedFavorites) {
			setFavoriteList(JSON.parse(storedFavorites));
			console.log("on load stored favorites", storedFavorites);
		}
	}, []);
	useEffect(() => {
		localStorage.setItem("favoritesWeather", JSON.stringify(favoriteList));
		console.log("list after state changed favorites", favoriteList);
	}, [favoriteList]);

	return data.length ? (
		<section>
			<div className="product-favorite">
				{data.map((item) => {
					return (
						<div key={item.id} className="product-item">
							<div className="product-container">
								{item.name}, {item.country}
							</div>
							<button
								onClick={() => {
									checkFavoriteWeather(item.name);
								}}
							>
								Add to Cart
							</button>
							<button onClick={removeFromFavorite}>Remove to Cart</button>
						</div>
					);
				})}
			</div>
		</section>
	) : (
		<section>
			<div className="product-favorite">No Favorites saved</div>
		</section>
	);
};

export default FavoritesWeatherList;
