import React, { useState } from "react";
import "../App.css";
import InputField from "./inputField_component";
import ButtonHeader from "./button_header";

const PalindromChecker = () => {
	const [word, setWord] = useState<string>("");
	const [isPalindrome, setIsPalindrom] = useState<boolean>(false);
	const handleTextChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		const { value } = e.target as unknown as { value: string };
		setWord(value);
	};
	const checkIsPalindrom = (e: MouseEvent) => {
		e.preventDefault();
		//const reg = /[\W_]/g;
		//console.log("check for number :", typeof word);
		const reversed = word.split("").reverse().join("");
		if (reversed === word) {
			setIsPalindrom(true);
			console.log("true");
		} else {
			setIsPalindrom(false);
			console.log("false");
		}
	};
	return (
		<div className="header">
			<div className="search-box">
				<InputField placeholder="Type a word" handleChange={handleTextChange} />
			</div>
			<div className="btn-box">
				<ButtonHeader btnName="Check word" handleClick={checkIsPalindrom} />
			</div>
			{isPalindrome === true ? (
				<div>
					<p></p>
				</div>
			) : (
				<div>
					<p>{word} is not a palidrome</p>
				</div>
			)}
		</div>
	);
};

export default PalindromChecker;
