import React, { useState, useEffect, useCallback } from "react";
import { useSelector } from "react-redux";
import { RootState } from "../redux/root-reducer";
import { weatherData } from "../redux/searchWeather/weatherActions";
import "../App.css";

const WeatherCard = () => {
	const [photo, setPhoto] = useState<string>("");
	let data = useSelector((state: RootState) => state.data);
	const fahrenheit: number = data.temp * 1.8 - 459.67;
	const celsius: number = data.temp - 273.15;
	//Clouds, Rain, Clear, Smoke, haze, dust, mist, sand, thunderstorm

	const weatherImg = useCallback(() => {
		let value;
		switch (data.weather.toLowerCase()) {
			case "rain" || "thunderstorm":
				value = require("../assets/rainy.png");
				break;
			case "clouds" || "smoke" || "haze" || "dust" || "sand" || "mist":
				value = require("../assets/cloud.png");
				break;
			case "clear":
				value = require("../assets/sunny.png");
				break;
			case "snow":
				value = require("../assets/snowflake.png");
				break;
			default:
				break;
		}
		setPhoto(value);
	}, [data.weather]);

	useEffect(() => {
		if (data) {
			weatherImg();
		}
	}, [data, weatherImg]);

	return (
		<div className="product-item">
			<div>{data.country}</div>
			<img
				src={require("../assets/sunny.png")}
				height="100px"
				width="100px"
				alt=""
			/>
			<div>{data.temp}</div>
			<div>{data.humidity} </div>
			<div>{data.wind} </div>
		</div>
	);
};

export default WeatherCard;
