import React, { useState, useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../redux/root-reducer";
import { weatherData } from "../redux/searchWeather/weatherActions";
import "../App.css";

const WeatherCard = () => {
	const [photo, setPhoto] = useState<string>("");
	let data = useSelector((state: RootState) => state.weatherReducer);
	const fahrenheit: number = data[0].temp * 1.8 - 459.67;
	const celsius: number = data[0].temp - 273.15;
	//Clouds, Rain, Clear, Smoke, haze, dust, mist, sand, thunderstorm

	const weatherImg = useCallback(() => {
		let value;
		switch (data[0].weather.toLowerCase()) {
			case "rain" || "thunderstorm":
				value = require("../assets/rainy.png");
				break;
			case "clouds" || "smoke" || "haze" || "dust" || "sand" || "mist":
				value = require("../assets/cloud.png");
				break;
			case "clear":
				value = require("../assets/sunny.png");
				break;
			case "snow":
				value = require("../assets/snowflake.png");
				break;
			default:
				break;
		}
		setPhoto(value);
	}, [data[0].weather]);

	useEffect(() => {
		if (data) {
			console.log("data in component state,", data);
		} else {
			console.log("noData");
		}
	}, [data]);

	return (
		<div className="product-item">
			<div>
				{data[0].name}, {data[0].country}
			</div>
			<img
				src={require("../assets/sunny.png")}
				height="100px"
				width="100px"
				alt=""
			/>
			<div>{data[0].temp}</div>
			<div>{data[0].humidity} </div>
			<div>{data[0].wind} </div>
		</div>
	);
};

export default WeatherCard;
