import React, { useState, useEffect, useCallback } from "react";
import { WeatherData } from "../dataTypes";
import "../App.css";

interface WeatherProps {
	data: WeatherData;
}

const WeatherCard = ({ data }: WeatherProps) => {
	const [photo, setPhoto] = useState<string>("");
	const fahrenheit: number = data.temp * 1.8 - 459.67;
	const celsius: number = data.temp - 273.15;
	//Clouds, Rain, Clear, Smoke, haze, dust, mist, sand, thunderstorm

	const weatherImg = useCallback(() => {
		let value;
		switch (data.weather.toLowerCase()) {
			case "rain" || "thunderstorm":
				value = require("../../assets/rainy.png");
				break;
			case "clouds" || "smoke" || "haze" || "dust" || "sand" || "mist":
				value = require("../../assets/clouds.png");
				break;
			case "clear":
				value = require("../../assets/sunny.png");
				break;
			case "snow":
				value = require("../../assets/smowflake.png");
				break;
			default:
				break;
		}
		setPhoto(value);
	}, [data.weather]);

	useEffect(() => {
		if (data) {
			weatherImg();
		}
	}, [data, weatherImg]);

	return (
		<div className="product-item">
			<div>London</div>
			<img
				src={require("../../assets/sunny.png")}
				height="100px"
				width="100px"
				alt=""
			/>
			<div>50 C</div>
			<div>50% </div>
			<div>15km/h </div>
		</div>
	);
};

export default WeatherCard;
